#streamlit run streamlit_basics.py

import streamlit as st
import time
from PIL import Image

st.title("Machine Learning Model")

# header
st.header('Introduction')

# subheader
st.subheader("this is subheader")

# text
st.text('this is text')

# read input from user
input_text =st.text_input("type something",'type here...')
st.text(input_text)


input_text=st.text_area("enter here", 'this is large text area')


## markdown
st.markdown('This text is ___really important___.')
st.markdown("## This is heading")

st.markdown("""1. First item
2. Second item
3. Third item
4. Fourth item""")

# button

button=st.button('click me')
if button:
    st.text("i am clicked")
    st.info("im clicked")
    st.toast("i am dissappear")
    st.warning("this is warning")
    st.error("this is error")
    st.success("this is success")

#image
#st.image("data:image/jpeg;base64,",width=400)

# check box
flag = st.checkbox("select me")
st.text(flag)
if flag:
    st.image("data:image/jpeg;base64,",width=400)
    

# radion button
selection = st.radio('choose your model',['nlp','image','ml'])
st.write(selection)

# select box
selectbox=st.selectbox('choose your model',['nlp','image','ml'])
st.write(selectbox)



# multi select
selectbox=st.multiselect('choose your model',['nlp','image','ml'])
st.write(selectbox)

# spinner
with st.spinner("Downloading"):
    st.write("download your model here")
    time.sleep(10)
    
# select numerical value from the given list 
st.slider("set thtreshold", 0,100, step=10)


